import React, { Component } from "react";
import Header from "./components/Header";

import './App.css';
import FriendCard from "./components/FriendCard";
import Wrapper from "./components/Wrapper";
import krustyPals from "./krustyPals.json"

class App extends Component {

  state = this.initialState;
  get initialState() {
    return {
      response: "Click an image below to begin!",
      score: 0,
      topScore: 0,
      image: "",
      krustyPals: krustyPals,
      display:{},
      clicked:["Not Clicked","Not Clicked","Not Clicked","Not Clicked","Not Clicked","Not Clicked","Not Clicked","Not Clicked","Not Clicked","Not Clicked","Not Clicked","Not Clicked"],
      trackTop: false,
      play: "Playing"
    }
  }

  resetBuilder() {
    let tempTopScore = this.state.topScore;
    this.setState(this.initialState);
    this.setState({ topScore: tempTopScore });
  }

  updateClicked(id, itemAttributes) {
    alert("update Clicked clled");
    alert("id "+ id);
    alert("itemAttributes "+ itemAttributes);

    var index = this.state.krustyPals.findIndex(x => x.id === id);
    if (index === -1) {
      // handle error
    } else {
      alert("My index is " + index);
      this.setState({
        krustyPals: [
          this.state.krustyPals.slice(0, index),
          Object.assign({}, this.state.krustyPals[index].clicked, itemAttributes),
          this.state.krustyPals.slice(index + 1)
        ]
      });
    }
  }

  resetClicked() {
    for (let i = 0; i < this.state.krustyPals.length; i++) {
      this.updateClicked(i, "Not Clicked");
    }
  }

  setScore = (clicked) => {

    if (this.state.play !== "Playing") {
      this.resetBuilder();
    }

    if (krustyPals[clicked - 1].clicked === "clicked") {
      this.setState({ response: "Sorry No, Gave Over!" })
      this.setState({ play: "Over" })
    } else {
      alert("krustyPals " + JSON.stringify(this.state.krustyPals));
      let newObj = this.state.krustyPals;
      newObj.clicked = this.state.clicked;
      this.setState()
      this.updateClicked((clicked - 1), "Clicked");
      this.setState({ score: this.state.score + 1 }, () => {
        if (this.state.score >= this.state.topScore) {
          this.setState({ topScore: this.state.score });
        }
        this.setState({ response: "CORRECT!!! Keep guessing" })
      });
      //randomize
      let newKrustyPals = [];
      let used = [];
      let newPosi = -1;
      while (used.length < this.state.krustyPals.length) {
        newPosi = Math.floor(Math.random() * 11);
        while (used.indexOf(newPosi) !== -1) {
          newPosi++;
          if (newPosi > 11) newPosi = 0;
        }
        newKrustyPals.push(this.state.krustyPals[newPosi]);
        used.push(newPosi);
      }
      this.setState({ krustyPals: newKrustyPals });
    }
  }


  render() {
    return (
      <Wrapper>
        <Header response={this.state.response} score={this.state.score} topScore={this.state.topScore} />
        {this.state.krustyPals.map(pal => (
          <FriendCard
            setScoreCallBack={this.setScore}
            image={pal.image}
            clicked={pal.clicked}
            id={pal.id}
            name={pal.name}
          />
        ))}
      </Wrapper>
    );
  }
}

export default App;
