import React from "react";
import "./style.css";


class FriendCard extends React.Component {

  handleScore = () => {
    this.setState({ score: this.state.score + 2 });
  }

  render() {
    return (
    <button className="btn btn-primary" onClick={this.handleScore}>
        Double
    </button>
    );
  }
}
export default FriendCard;
